inp = inp_s if sam else inp_o

g = [list(l) for l in inp.splitlines()]
r = []
for y in range(len(g)):
    if all(g[y][x] == '.' for x in range(len(g[0]))):
        r.append(y)
c = []
for x in range(len(g[0])):
    if all(g[y][x] == '.' for y in range(len(g))):
        c.append(x)

g2 = []
for y in range(len(g)):
    if y in r:
        g2.append(['.'] * (len(g[0]) + len(c)))
    a = []
    for x in range(len(g[0])):
        a.append(g[y][x])
        if x in c:
            a.append('.')
    g2.append(a)
g = g2

ps = []
h = len(g)
w = len(g[0])
for y in range(h):
    for x in range(w):
        if g[y][x] == '#':
            ps.append((y, x))

ans = 0
for i in range(len(ps)):
    for j in range(i + 1, len(ps)):
        sy, sx = ps[i]
        ty, tx = ps[j]
        ans += abs(sy - ty) + abs(sx - tx)
print(ans)
